import { Component } from '@angular/core';
import { GameService } from './../game.service';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {
  userChoice: string = '';
  computerChoice: string = '';
  gameResult: string = '';

  constructor(private gameService: GameService) {}

  playGame(choice: string) {
    this.gameService.playGame(choice).subscribe((data) => {
      this.userChoice = data.userChoice;
      this.computerChoice = data.computerChoice;
      this.gameResult = data.result;
    });
  }
}
<p>Game works!</p>
<div class="container mt-5">
  <div class="row">
    <div class="col-lg-4 text-left">
      <p>User's Choice: {{ userChoice }}</p>
    </div>
    <div class="col-lg-4 text-center">
      <button (click)="playGame('ROCK')" class="btn btn-link mx-2">Rock</button>
      <button (click)="playGame('SCISSORS')" class="btn btn-link mx-2">Scissors</button>
      <button (click)="playGame('PAPER')" class="btn btn-link mx-2">Paper</button>
    </div>
    <div class="col-lg-4 text-right">
      <p>Computer's Choice: {{ computerChoice }}</p>
    </div>
  </div>
  <p>Result: {{ gameResult }}</p>
</div>
