import { Component } from '@angular/core';
import { GameService } from './../game.service';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {
  userChoice: string = '';
  computerChoice: string = '';
  gameResult: string = '';

  constructor(private gameService: GameService) {}

  playGame(choice: string) {
    this.gameService.playGame(choice).subscribe((data) => {
      this.userChoice = data.userChoice;
      this.computerChoice = data.computerChoice;
      this.gameResult = data.result;
    });
  }
}
<div>
  <h1>Welcome</h1>
  <h2>Game of Rock, Paper and Scissors </h2>
  <h2>Choose one of the options to start playing</h2>
  <h3>Have fun!!!</h3>
</div>
<div class = "user">
  <div class="selection">
      <button (click)="playGame('ROCK')" class="fas fa-hand-rock fa-lg" id="rock"> Rock </button>
      <button (click)="playGame('SCISSORS')" class="fas fa-hand-paper fa-lg" id="paper"> Paper </button>
      <button (click)="playGame('PAPER')" class="fas fa-hand-scissors fa-lg" id="scissors"> Scissor </button>
  </div>
     
  <div class="col-lg-3">
      <p>User's Choice: {{ userChoice }}</p>
  </div> 
  <div class="col-lg-5">
      <p>Computer's Choice: {{ computerChoice }}</p>
  </div>
</div>
<div class="container mt-5 row">          
  <p>Result: {{ gameResult }}</p>
</div>
