import { Component } from '@angular/core';
import { GameService } from './../game.service';

import { library } from '@fortawesome/fontawesome-svg-core';
import { faHandBackFist, faHandPaper, faHandScissors, fas } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {
  userChoice: string = '';
  computerChoice: string = '';
  gameResult: string = '';
  rockIcon = faHandBackFist;

  constructor(private gameService: GameService) {}

  playGame(choice: string) {
    this.gameService.playGame(choice).subscribe((data) => {
      this.userChoice = data.userChoice;
      this.computerChoice = data.computerChoice;
      this.gameResult = data.result;
    });
  }
}
<div>
  <h2>Welcome to Rock-Paper-Scissors world</h2>
  <h2>Choose one of the options to start playing</h2>
  <h3>Have fun!!!</h3>
</div>
<div class = "user">
  <div class="selection">
    <button (click)="playGame('ROCK')"> ROCK </button>
    <button (click)="playGame('PAPER')"> PAPER </button>
    <button (click)="playGame('SCISSORS')"> SCISSORS </button>
  </div>
     
  <div class="col-lg-4 text-left">
      <p>User's Choice: {{ userChoice }}</p>
  </div> 
  <div class="col-lg-5 text-right">
      <p><span>Computer's Choice:</span> {{ computerChoice }}</p>
  </div>
</div>
<div class="container mt-3 row">          
  <p>Result: {{ gameResult }}</p>
</div>
